schemas:
DEFINE TABLE meta SCHEMALESS
    PERMISSIONS
        FOR create, select FULL
        FOR update, delete WHERE id = $auth.id;

DEFINE FIELD created_by ON meta TYPE record<person>;
DEFINE FIELD created_on ON meta TYPE datetime DEFAULT time::now();
DEFINE FIELD modified_by ON meta TYPE option<record<person>> DEFAULT NONE;
DEFINE FIELD modified_on ON meta TYPE option<datetime> DEFAULT NONE;
DEFINE FIELD deleted_by ON meta TYPE option<record<person>> DEFAULT NONE;
DEFINE FIELD deleted_on ON meta TYPE option<datetime> DEFAULT NONE;
DEFINE FIELD data ON meta TYPE option<object> DEFAULT NONE;

DEFINE TABLE person SCHEMALESS
    PERMISSIONS
        FOR select FULL
        FOR update WHERE id = $auth.id
        FOR delete WHERE id = $auth.id
        FOR create FULL;

DEFINE FIELD username ON person TYPE string;
DEFINE FIELD email ON person TYPE string ASSERT string::is::email($value);
DEFINE FIELD pass_hash ON person TYPE string;
DEFINE FIELD is_admin ON person TYPE bool DEFAULT false;
DEFINE FIELD meta ON person TYPE record<meta>;

DEFINE TABLE post SCHEMALESS
    PERMISSIONS
        FOR select FULL
        FOR create WHERE permission:create_post IN $auth.permissions
        FOR update, delete WHERE author = $auth.id;

DEFINE FIELD title ON post TYPE string;
DEFINE FIELD markdown ON post TYPE string;
DEFINE FIELD author ON post TYPE record<person>;
DEFINE FIELD meta ON post TYPE record<meta>;

DEFINE TABLE script_migration SCHEMALESS
    PERMISSIONS
        FOR select FULL
        FOR create, update, delete NONE;

DEFINE FIELD script_name ON script_migration TYPE string;
DEFINE FIELD executed_at ON script_migration TYPE datetime DEFAULT time::now();

DEFINE TABLE nb_token SCHEMALESS
    PERMISSIONS
        FOR select, update, delete, create FULL;

DEFINE FIELD person ON nb_token TYPE record<person>;
DEFINE FIELD meta ON nb_token TYPE record<meta>;
